		UPDATE items 
    	SET drink = #{drink}, code = #{code}, price = #{price}, stock = #{stock}, 
      	ideal_stock_weekday = #{ideal_stock_weekday}, ideal_stock_weekend = #{ideal_stock_weekend}, 
